CHATGLM2_PROMPT_FORMAT = "Question: {question}\n\nAnswer:"
LLAMA2_PROMPT_FORMAT = """### HUMAN:
{question}

### RESPONSE:
"""

def completion(prompt, max_length):
    import requests
    url = "http://127.0.0.1:8000/v2/completions"
    headers = {"Content-Type": "application/json"}
    data = {"prompt": prompt,
            "max_length": max_length,
            "temperature": float(0),
            "history": []}

    response = requests.post(url, headers=headers, json=data)

    if response.status_code == 200:
        data = response.json()
        print(data)
    else:
        print("Request failed with status code:", response.status_code)


def reinit(framework, model_name, model_path, model_dtype, device, n_threads, warmup):
    import requests
    url = "http://127.0.0.1:8000/v2/reinit"
    headers = {"Content-Type": "application/json"}
    data = {"model_name": model_name,
            "framework": framework,
            "model_path": model_path,
            "model_dtype": model_dtype,
            'device': device,
            'n_threads': n_threads,
            'warmup': warmup,
            }
    response = requests.post(url, headers=headers, json=data)

    if response.status_code == 200:
        print(response.json())
    else:
        print("Request failed with status code:", response.status_code)


