def benchmark(max_length):
    global prompt_dict
    steps = 12
    warm = 2
    bench_list = [
        # ["transformers", "chatglm2-6b", "int4", "/home/sdp/models/chatglm2-6b"],
        # ["transformers", "", "llama2-7b", "int4", "/home/sdp/models/chatglm2-6b"],
        ["bigdl-llm-transformers", "chatglm2-6b",
            "int4", "/home/sdp/models/Llama-2-7b-hf"],
        ["bigdl-llm-transformers", "llama2-7b",
            "int4", "/home/sdp/models/Llama-2-7b-hf"],
    ]
    for item in bench_list:
        print('-'*20, item, '-'*20)
        reinit(framework=item[0], model_name=item[1],
               model_dtype=item[2], model_path=item[3])
        for key, value in prompt_dict.items():
            if key == "32" or key == "128" or key == "512":
                lt = []
                for i in range(steps):
                    result = completion(prompt=value, max_length=max_length)
                    lt.append(result["latency_per_token"])
                print(key, ": ", sum(lt[warm:])/len(lt[warm:]), f"/t{lt}")


if __name__ == "__main__":
    parser = argparse.ArgumentParser(description='client to test service api')
    parser.add_argument('-t', '--type', type=str, default="generate",
                        choices=["completion", "reinit", "benchmark"],
                        help='service type of LLM server to ping')
    parser.add_argument('-p', '--prompt', type=str, default="what is AI?",
                        help='prompt')
    parser.add_argument('-x', '--max-length', type=int,
                        default=128,
                        help='max generation token length')
    parser.add_argument('-i', '--prompt-index', type=str,
                        help='prompt')
    parser.add_argument('-f', '--reinit-framework', type=str,
                        help='framework to be reinited')
    parser.add_argument('-m', '--reinit-model', type=str,
                        help='model to be reinited')
    parser.add_argument('-l', '--reinit-model-path', type=str,
                        help='model path')
    parser.add_argument('-d', '--reinit-dtype', type=str,
                        help='datatype to be reinited')
    parser.add_argument('-n', '--reinit-n-threads', type=int,
                        help='number of thread')
    parser.add_argument('-e', '--reinit-device', type=str, 
                        choices=["CPU", "GPU"], default="CPU", 
                        help='device for inference')
    parser.add_argument('-w', '--reinit-warmup', action="store_true", help='warmup model')

    args = parser.parse_args()

    if args.prompt_index is not None:
        prompt = prompt_dict[args.prompt_index]
    if args.type == "completion":
        completion(args.prompt, args.max_length)
    elif args.type == "reinit":
        reinit(framework=args.reinit_framework,
               model_name=args.reinit_model,
               model_path=args.reinit_model_path,
               model_dtype=args.reinit_dtype,
               device=args.reinit_device,
               n_threads=args.reinit_n_threads,
               warmup=args.reinit_warmup)
    elif type == "benchmark":
        benchmark(args.max_length)
